class Consumer : IRunnable {
	private IBlockingQueue queue;
	Consumer(IBlockingQueue q) { queue = q; }
	public void Run() {
		try {
			while (true) { Consume(queue.Take()); }
		} catch (InterruptedException ex) { ... handle ...}
	}
	void Consume(object x) { ... }
}

class Setup {
